/**
 * Busca peças compatíveis com um veículo específico
 */
export function findPartsForVehicle(
  brand: string,
  model: string,
  year: number,
  engineCode?: string
): RealPart[] {
  const normalizedBrand = brand.toLowerCase().trim();
  const normalizedModel = model.toLowerCase().trim();
  
  return ALL_REAL_PARTS.filter(part => {
    return part.compatibility.some(comp => {
      const brandMatch = comp.brand.toLowerCase().includes(normalizedBrand) ||
                        normalizedBrand.includes(comp.brand.toLowerCase());
      const modelMatch = comp.model.toLowerCase().includes(normalizedModel) ||
                        normalizedModel.includes(comp.model.toLowerCase());
      const yearMatch = year >= comp.yearStart && year <= comp.yearEnd;
      
      // Se engineCode foi fornecido, verificar compatibilidade
      if (engineCode && comp.engineCode) {
        const engineMatch = comp.engineCode.toLowerCase().includes(engineCode.toLowerCase()) ||
                           engineCode.toLowerCase().includes(comp.engineCode.toLowerCase());
        return brandMatch && modelMatch && yearMatch && engineMatch;
      }
      
      return brandMatch && modelMatch && yearMatch;
    });
  });
}

/**
 * Busca uma peça pelo código OEM
 */
export function findPartByOemCode(oemCode: string): RealPart | undefined {
  const normalizedCode = oemCode.replace(/[\s-]/g, '').toLowerCase();
  return ALL_REAL_PARTS.find(part => 
    part.oemCode.replace(/[\s-]/g, '').toLowerCase() === normalizedCode
  );
}

/**
 * Busca peças por código equivalente (aftermarket)
 */
export function findPartsByEquivalentCode(code: string): RealPart[] {
  const normalizedCode = code.replace(/[\s-]/g, '').toLowerCase();
  return ALL_REAL_PARTS.filter(part =>
    part.equivalents.some(eq => 
      eq.code.replace(/[\s-]/g, '').toLowerCase() === normalizedCode
    )
  );
}


/**
 * Retorna estatísticas da base de dados
 */
export function getDatabaseStats(): {
  totalParts: number;
  partsByBrand: Record<string, number>;
  partsByCategory: Record<string, number>;
  totalEquivalents: number;
  averageEquivalentsPerPart: number;
} {
  const partsByBrand: Record<string, number> = {};
  const partsByCategory: Record<string, number> = {};
  let totalEquivalents = 0;

  ALL_REAL_PARTS.forEach(part => {
    // Contar por marca do fabricante
    const brand = part.manufacturer.split('/')[0];
    partsByBrand[brand] = (partsByBrand[brand] || 0) + 1;

    // Contar por categoria
    partsByCategory[part.category] = (partsByCategory[part.category] || 0) + 1;

    // Contar equivalentes
    totalEquivalents += part.equivalents.length;
  });

  return {
    totalParts: ALL_REAL_PARTS.length,
    partsByBrand,
    partsByCategory,
    totalEquivalents,
    averageEquivalentsPerPart: totalEquivalents / ALL_REAL_PARTS.length,
  };
}

/**
 * Retorna todas as marcas de veículos disponíveis na base
 */
export function getAvailableVehicleBrands(): string[] {
  const brands = new Set<string>();
  ALL_REAL_PARTS.forEach(part => {
    part.compatibility.forEach(comp => {
      brands.add(comp.brand);
    });
  });
  return Array.from(brands).sort();
}

/**
 * Retorna todos os modelos disponíveis para uma marca
 */
export function getAvailableModels(brand: string): string[] {
  const models = new Set<string>();
  const normalizedBrand = brand.toLowerCase();
  
  ALL_REAL_PARTS.forEach(part => {
    part.compatibility.forEach(comp => {
      if (comp.brand.toLowerCase() === normalizedBrand) {
        models.add(comp.model);
      }
    });
  });
  return Array.from(models).sort();
}

/**
 * Retorna todas as categorias de peças disponíveis
 */
export function getAvailableCategories(): PartCategory[] {
  const categories = new Set<PartCategory>();
  ALL_REAL_PARTS.forEach(part => {
    categories.add(part.category);
  });
  return Array.from(categories);
}

/**
 * Busca peças por categoria
 */
export function findPartsByCategory(category: PartCategory): RealPart[] {
  return ALL_REAL_PARTS.filter(part => part.category === category);
}

/**
 * Busca peças por nome (busca parcial)
 */
export function searchPartsByName(searchTerm: string): RealPart[] {
  const normalizedTerm = searchTerm.toLowerCase().trim();
  return ALL_REAL_PARTS.filter(part =>
    part.name.toLowerCase().includes(normalizedTerm)
  );
}

/**
 * Retorna peças com alta confiança (score >= 90)
 */
export function getHighConfidenceParts(): RealPart[] {
  return ALL_REAL_PARTS.filter(part => part.confidenceScore >= 90);
}

